mapper.select:
select
id, user_id, amount, balance, dateline, type, outer_id
from nxt_transaction


        and id = #{id}


        and user_id = #{userId}


        and amount = #{amount}


        and balance = #{balance}


        and dateline = #{dateline}


        and type = #{type}


        and outer_id = #{outerId}

select
  id, user_id, amount, balance, dateline, type, outer_id
from nxt_transaction


        and user_id = #{userId}


        and type = #{type}


order by id desc
limit #{offset}, #{limit}

